const Header = (props) => {
    const menuItems = [
        'All',
        'Javascript',
        'Ruby',
        'Java',
        'Css',
        'Python'
    ]

    return (<div>
        <Container>
            <Nav>
                {menuItems.map((item, key) => <Nav.Item key={key}><Nav.Link>{item}</Nav.Link></Nav.Item>)}
            </Nav>
        </Container>
    </div>)
}

